import React from 'react'
import AccountMenu from './account/AccountMenu.js'

class Accounts extends React.Component {

  state={
    selected: 'accountMain',
  }

  selected=(value)=>{
    this.setState({
    selected: value
    })
  }

  renderSwitch(param) {
    switch(param) {
        case 'accountMain':
            return (
            <div>
                <NavBar selectedLink={this.props.selectedLink}/>
                <AccountMenu selected={this.selected} selectedLink={this.props.selectedLink}/>   
            </div>
            )
        case 'addAccount':
            return <AddAccount user={this.props.user} selected={this.selectedLink}/>
        case 'editAccount': 
            return <EditAccount user={this.props.user} selected={this.selectedLink}/>
        // case 'newTicket': 
        //     return <NewTicket user={this.state.user} selectedLink={this.selectedLink}/>
        default: 
            return null; 
    }
}


  render() {
    return(
      <div>
        { localStorage.token
        ? 
            <div>
                {this.renderSwitch(this.state.selected)}  
          </div>
        : <Login/>
        }  
      </div>
    )
  }

}

export default Accounts;
