import React from 'react'

class EditPrices extends React.Component {


  updatePrices = (e) => {

    e.preventDefault()

    let target   = e.target;
    let formData = {};

    for (let i = 0; i < target.length-1; i++) {
        formData[target.elements[i].id] = target.elements[i].value;
    }
    console.log('formData', formData);

    const isNumber = value => typeof value === 'number' && value === value && value !== Infinity && value !== -Infinity

    for (const item in formData) {
      if(Number.isFinite(formData[item])){
      // if(!isNumber(formData[item])){
        return alert("Invalid Input", formData[item].id)
      }
    }


    // debugger

    // fetch(`http://localhost:3000/api/v1/items`,
    // {
    //   method: "POST",
    //   headers: {
    //     "Authorization": `Bearer ${localStorage.token}`,
    //     "Content-Type" : "application/json"
    //   },
    //   body: JSON.stringify({
    //     prices: data,
    //     id: localStorage.user_id
    //     })
    // })
    // .then(res => res.json())
    // .then(user => this.props.updateUser(user))
  }


  goHome = () => {
      this.props.selectedLink("main")
  }



  render(){
  return(
      <div>
          <form onSubmit={(e) => {
              this.updatePrices(e)
              }}>
              {this.props.user.items.map(item => 
                <div>
                    <label>{item.item_type} - {item.name}</label>
                    <input className="ui label input" type='text' id={item.id} placeholder={item.price} defaultValue={item.price}/>
                    {/* <input className="ui button" type='submit' value='Update Price'/> */}
                </div> 
              )} 
              <input className="ui button" type='submit' value='Update Prices'/>  
          </form>

          <br/>
          <br/>
          <p onClick={()=>this.goHome()} className="ui massive button">
              HOME
          </p>
      </div>
  )}

}

  export default EditPrices;
