import React from 'react'

class Buttons extends React.Component {

    state={
        items: this.props.user.items,

        buttonMenu: 'category',

        colorDescriptions : ["white","black","blue",'red', 'tan', 'brown', 'gray', 'pink'],

        fabricDescriptions : ['silk', 'linen', 'rayon', 'wool']

    }

    buttonMenuSelect=(value)=>{
        this.setState({
        buttonMenu: value
        })
    }

    typeSelect=(item)=>{
        this.props.setCurrentItem(item)
        this.buttonMenuSelect('description')
    }

    newItem = () => {
        this.props.newItem()
        this.buttonMenuSelect('')
    }


    renderSwitch(param) {
        switch(param) {
            case 'category':
                return (
                <div>
                    <button onClick={()=>this.buttonMenuSelect('laundry')}>Laundry</button>
                    <button onClick={()=>this.buttonMenuSelect('dryclean')}>DryClean</button>

                    bottom menu buttons
                </div>
                )
            case 'laundry':
                return this.state.items.filter(item => item.item_type === "laundry").map(item => <button onClick={()=>{ this.typeSelect(item)}} >{item.name}</button>)
            case 'dryclean':
                return this.state.items.filter(item => item.item_type === "dry clean").map(item => <button onClick={()=>{ this.typeSelect(item)}} >{item.name}</button>)
            case 'description':
                return (
                    <div>
                        {this.state.colorDescriptions.map(color => <button onClick={()=>{ this.props.addDescription(color)}} >{color}</button>)}
                        {this.state.fabricDescriptions.map(fabric => <button onClick={()=>{ this.props.addDescription(fabric)}} >{fabric}</button>)}
                        <button onClick={()=> this.props.newItem()}>NEW ITEM</button>
                    </div>
                )
            default: 
                return null; 
        }
    }


      render() {
        return(
          <div>
            {  
                <div>
                    {this.renderSwitch(this.state.buttonMenu)}  
              </div>

            }  
          </div>
        )
      }




  }

  export default Buttons;
