import React from 'react'
import NavBar from './NavBar.js'
import Login from './Login.js'
import EditProfile from './EditProfile.js'
import MainMenu from './MainMenu.js'
import Accounts from './Accounts.js'
import NewTicket from './NewTicket.js'

class Dash extends React.Component{


    state={
        selected: 'main',
        user: {}
    }



  componentDidMount(){
    fetch(`http://localhost:3000/api/v1/users/${localStorage.user_id}`,
    {
      method: "GET",
      headers: {
        Authorization: `Bearer ${localStorage.token}`
      } 
    })
    .then(res => res.json())
    .then(user => {this.setState({
      user: user 
    })}
    )
    .then(this.selectedLink("main"))
  }


  selectedLink=(value)=>{
      this.setState({
      selected: value
      })
  }

  renderSwitch(param) {
        switch(param) {
            case 'main':
                return (
                <div>
                    <NavBar selectedLink={this.selectedLink}/>
                    <MainMenu selectedLink={this.selectedLink}/>   
                </div>
                )
            case 'editProfile':
                return <EditProfile user={this.state.user} selectedLink={this.selectedLink}/>
            case 'accounts': 
                return <Accounts user={this.state.user} selectedLink={this.selectedLink}/>
            case 'newTicket': 
                return <NewTicket user={this.state.user} selectedLink={this.selectedLink}/>
            default: 
                return null; 
        }
    }



  render() {
    return(
      <div>
         { localStorage.token
         ? 
            <div>
                {this.renderSwitch(this.state.selected)}  
           </div>
         : <Login/>
         }  
      </div>
    )
  }

}

export default Dash
