import React from 'react'

class Buttons extends React.Component {

    state={
        items: this.props.user.items,

        buttonMenu: 'main'
    }

    buttonMenuSelect=(value)=>{
        this.setState({
        buttonMenu: value
        })
    }

    typeSelect=(item)=>{
        this.props.setCurrentItem(item)
        this.buttonMenuSelect('description')
    }

    renderSwitch(param) {
        switch(param) {
            case 'main':
                return (
                <div>
                    <div>
                        <button onClick={this.buttonMenuSelect()}>Laundry</button>
                        <button>DryClean</button>
                    </div>
                    <div>
                        bottom menu buttons
                    </div>
                </div>
                )
            case 'laundry':
                return this.state.items.filter(item => item.item_type === "laundry").map(item => <button onClick={()=>{ this.typeSelect(item)}} >{item.name}</button>)
            case 'dryclean':
                return this.state.items.filter(item => item.item_type === "dry clean").map(item => <button onClick={()=>{ this.typeSelect(item)}} >{item.name}</button>)
            case 'description':
                return <h1>description buttons</h1>
            default: 
                return null; 
        }
    }


      render() {
        return(
          <div>
            {  
                <div>
                    {this.renderSwitch(this.state.selected)}  
              </div>

            }  
          </div>
        )
      }




  }

  export default Buttons;
