import React from 'react'
import NavBar from './NavBar.js'
import Login from './Login.js'

class Dash extends React.Component{


    state={
        selected: 'match'
    }

  selectedLink=(value)=>{
    this.setState({
      selected: value
    })
  }

  componentDidMount(){
    fetch(`http://localhost:3000/api/v1/users/${localStorage.user_id}`,
    {
      method: "GET",
      headers: {
        Authorization: `Bearer ${localStorage.token}`
      } 
    })
    .then(res => res.json())
    .then(user => {this.setState({
      user: user 
    })}
    )

  }

  render() {
    return(
      <div>
         { localStorage.token
         ? <div>
              <NavBar selectedLink={this.selectedLink}/>
              {/* <MainContainer selection={this.state.selected} users={this.state.users} selectedLink={this.selectedLink} matches={this.state.matches}/> */}
           </div>
         : <Login/>
         }  
      </div>
    )
  }

}

export default Dash
