import React from 'react';

import 'semantic-ui-css/semantic.min.css';
import Login from './Login'

import { NavLink} from 'react-router-dom';



class App extends React.Component{

  constructor(){
    super()
    this.state = {
      testNew: "testnew",
      test: "test",
      form: false,
      user: {}
    }
  }

  addPainting = (e) => {
    e.preventDefault()

    let newPainting = {
      title: e.target[0].value,
      image: e.target[1].value,
      artist: {
        name: e.target[2].value
      }
    }

    let newArr = [...this.state.paintings, newPainting]

    this.setState({
      paintings: newArr
    }) 
  }

  getUserInfo = () => {
      fetch(`http://localhost:3000/api/v1/users/${localStorage.user_id}`,
      {
        method: "GET",
        headers: {
          Authorization: `Bearer ${localStorage.token}`
        } 
      })
      .then(res => res.json())
      .then(user => {this.setState({
        user: user 
      })}
      )
  }

  render(){

    return (
    <div>
      <Login/>
      <br></br>
      <NavLink
        to="/signup"
        exact

        activeStyle={{
            background: 'darkblue'
        }}
        > Signup
      </NavLink>
    </div>

  )};

}

export default App;
