import React from 'react'

class EditPrices extends React.Component {


  updatePrices = (e) => {

    e.preventDefault()

    const data = new FormData(e.target);

    console.log(data)
    // debugger

    fetch(`http://localhost:3000/api/v1/items`,
    {
      method: "PATCH",
      headers: {
        "Authorization": `Bearer ${localStorage.token}`,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        items: data,
        id: localStorage.user_id
        })
    })
    .then(res => res.json())
    .then(user => this.props.updateUser(user))
  }


  goHome = () => {
      this.props.selectedLink("main")
  }



  render(){
  return(
      <div>
          <form onSubmit={(e) => {
              this.updatePrices(e)
              }}>
              {this.props.user.items.map(item => 
                <div>
                    <label>{item.item_type} - {item.name}</label>
                    <input className="ui label input" type='text' id={item.id} defaultValue={item.price}/>
                    {/* <input className="ui button" type='submit' value='Update Price'/> */}
                </div> 
              )} 
              <input className="ui button" type='submit' value='Update Prices'/>  
          </form>

          <br/>
          <br/>
          <p onClick={()=>this.goHome()} className="ui massive button">
              HOME
          </p>
      </div>
  )}

}

  export default EditPrices;
