import React from 'react'

class EditPrices extends React.Component {


  updatePrice = (e) => {

    e.preventDefault()


    fetch(`http://localhost:3000/api/v1/items/${e.target[0].value}`,
    {
      method: "PATCH",
      headers: {
        "Authorization": `Bearer ${localStorage.token}`,
        "Content-Type" : "application/json"
      },
      body: JSON.stringify({
        price: e.target[1].value,
        })
    })
    .then(res => res.json())
    .then(user => this.props.updateUser(user))


  }

  goHome = () => {
      this.props.selectedLink("main")
  }



  render(){
  return(
      <div>
          <form onSubmit={(e) => {
              this.updatePrice(e)
              }}>
              {this.props.user.items.map(item => {
                <div>
                <input className="ui label input" type='number' value={item.price}/>
                <input className="ui button" type='submit' value='Update Price'/>
              </div> })}   
          </form>

          <br/>
          <br/>
          <p onClick={()=>this.goHome()} className="ui massive button">
              HOME
          </p>
      </div>
  )}

}

  export default EditPrices;
