import React from 'react'
import ItemList from './TicketItemList'
import Buttons from './TicketButtons'

class CreateTicket extends React.Component {

    state={

        selectingDescription : false,

        account : this.props.account,
        currentItem: {},
        currentItemDescription: [],
        currentItemPrice: 0,

        itemList:[],

        totalPrice: 0

    }

    setCurrentItem = (item) => {
        this.setState({
            currentItem: item
        })
    }

    addDesciption = (description) => {
        this.setState({
            currentItemDescription: [...this.state.currentItemDescription, description]
        })

    }



    render(){

      return (

      <div>

        <div>
            <ItemList user={this.props.user} itemList={this.state.itemList}/>
        </div>
        <div>
            <Buttons user={this.props.user} setCurrentItem={this.setCurrentItem}/>
        </div>

      <button onClick={()=>this.props.mainMenu('main')}>HOME</button>
      </div>



      )
    }
  }

  export default CreateTicket;
