      query: '',
      results: this.props.user.accounts,
      accounts: this.props.user.accounts,
    }

    getInfo = () => {

        let result = this.state.accounts.filter(a=> {
            a.last_name.toLowerCase().includes(this.state.query.toLowerCase()) || a.first_name.toLowerCase().includes(this.state.query.toLowerCase())})

        // debugger

        this.setState({
        results: result
        })
    }

    handleInputChange = () => {

        this.setState({
            query: this.search.value
        }, () => {
            if (this.state.query && this.state.query.length > 0) {
            //   if (this.state.query.length % 2 === 0) {
                this.getInfo()
            //   }
            } else if (!this.state.query) {
            }
        })
    }

    submitAccount = (e) => {
        e.preventDefault()
        console.log('submitting account')

        debugger

        const account = this.state.accounts.filter(account => account.id == e.target[0].value)

        this.props.selectedAccount(account)
        this.props.selected('createTicket')

    }

    render() {
        let divstyle = {position: 'absolute',
                left: '50%',
                top: '50%',
                transform: 'translate(-50%, -50%)',}
      return (
        <div style={divstyle}> 
            <input style={{minWidth: '230px'}}
                placeholder="Search By Last Name..."
                ref={input => this.search = input}
                onChange={this.handleInputChange}
            />
            <Suggestions results={this.state.results} submit={this.submitAccount} />
        </div>
      )
    }
  }

  export default AccountSelect
