        id:0,

        selectingDescription : false,

        account : this.props.account,
        currentItem: {
            id: 0,
            name: '',
            item_type: '',
            price: 0,
            description: []
        },

        itemList:[{}],

    } 

    setCurrentItem = (item) => {

        this.setState({
            currentItem: {...item, id: id}
        })

    }

    addDesciption = (description) => {

        let fullDescription = [...this.state.currentItem.description, description]

        this.setState({
            currentItem: {...this.state.currentItem, description: fullDescription}
         })
    }

    addOrder = () => {

        let order = {...this.state.currentItem, ...this.state.currentItemDescription }

        this.setState({
            itemList: [...this.state.itemList, order]
        })
        this.setState({
            currentItem: {}, 
            currentItemDescription: []
        })
    }



    render(){

      return (

      <div>

        <div>
            <ItemList user={this.props.user} itemList={this.state.itemList}/>
        </div>
        <div>
            <Buttons user={this.props.user} setCurrentItem={this.setCurrentItem}/>
        </div>

      <button onClick={()=>this.props.mainMenu('main')}>HOME</button>
      </div>



      )
    }
  }

  export default CreateTicket;
