import React from 'react'
import ItemList from './TicketItemList'
import Buttons from './TicketButtons'

class CreateTicket extends React.Component {

    state={

        ids:0,

        account : this.props.account,

        currentItem: {
            id: 0,
            name: '',
            item_type: '',
            price: 0,
            description: []
        },

        itemList:[],

    } 

    setCurrentItem = (item) => {

        this.setState({
            ids: this.state.ids + 1
        })

        this.setState({
            currentItem: {
                id: this.state.ids,
                name: item.name,
                item_type: item.item_type,
                price: item.price
            }
        })

        this.setState((state) => ({
            itemList: [...state.itemList, state.currentItem]
        }))

    }

    addDesciption = (description) => {

        let fullDescription = [...this.state.currentItem.description, description]


        this.setState({
            currentItem: {...this.state.currentItem, description: fullDescription}
        })

        let itemsArray = [...this.state.itemList]
        let itemID = itemsArray.findIndex(obj => obj.id === this.state.currentItem.id)

        itemsArray[itemID].description = fullDescription

        this.setState({
            itemList: itemsArray
        })

    }

    addOrder = () => {

        this.setState({
            currentItem: {
                id: this.state.ids,
                name: '',
                item_type: '',
                price: 0,
                description: []
            } 
        })

    }



    render(){

      return (

      <div>

        <div>
            <ItemList user={this.props.user} itemList={this.state.itemList}/>
        </div>
        <div>
            <Buttons user={this.props.user} setCurrentItem={this.setCurrentItem} addDesciption={this.addDesciption}/>
        </div>

      <button onClick={()=>this.props.mainMenu('main')}>HOME</button>
      </div>



      )
    }
  }

  export default CreateTicket;
