import React from 'react'

class Orders extends React.Component {


  renderTableData() {
    return this.props.user.map((user, index) => {
       const { id, item, item_type, description, cost, ticket_id, created_at, updated_at } = user.order //destructuring
       return (
          <tr key={id}>
             <td>{id}</td>
             <td>{item}</td>
             <td>{cost}.00</td>
             {/* <td>{description.join(" ")}</td> */}
             <td>{item_type}</td>
             <td>{cost}</td>
             <td>{ticket_id}</td>
          </tr>
       )
    })
 }

 renderTableHeader() {
  let header = Object.keys(this.props.user.orders[0])
  return header.map((key, index) => {
     return <th key={index}>{key.toUpperCase()}</th>
  })
}

    render(){

      return (

      <div >
          <h1 id='title'>Orders</h1>
            <table id='orders'>
               <tbody>
               <tr>{this.renderTableHeader()}</tr>
                  {this.renderTableData()}
               </tbody>
            </table>
      </div>

      )
    }
  }

  export default Orders;
