        itemList:{},

        totalPrice: 0

    }

    setCurrentItem = (item) => {
        this.setState({
            currentItem: item
        })
    }

    addDesciption = (description) => {
        this.setState({
            currentItemDescription: [...this.state.currentItemDescription, description]
         })
    }

    clearItemAndDescription = () => {
        this.setState({
            itemList: {currentItem : currentItemDescription}
        })
        this.setState({
            currentItem: {}, 
            currentItemDescription: []
        })
    }



    render(){

      return (

      <div>

        <div>
            <ItemList user={this.props.user} itemList={this.state.itemList}/>
        </div>
        <div>
            <Buttons user={this.props.user} setCurrentItem={this.setCurrentItem}/>
        </div>

      <button onClick={()=>this.props.mainMenu('main')}>HOME</button>
      </div>



      )
    }
  }

  export default CreateTicket;
